/*Table structure for table `mdm_inventory_setting` */

DROP TABLE IF EXISTS `mdm_inventory_setting`;

CREATE TABLE `mdm_inventory_setting` (
  `id` varchar(32) NOT NULL COMMENT '唯一编码',
  `dept` varchar(32) DEFAULT NULL COMMENT '所属门店',
  `dept_name` varchar(32) DEFAULT NULL COMMENT '门店名称',
  `site` varchar(32) DEFAULT NULL COMMENT '公司',
  `site_name` varchar(32) DEFAULT NULL COMMENT '公司名称',
  `code` varchar(32) DEFAULT NULL COMMENT '物料编码',
  `name` varchar(32) DEFAULT NULL COMMENT '物料名称',
  `default_warehourse_type` varchar(32) DEFAULT NULL COMMENT '默认仓库类型',
  `model` varchar(32) DEFAULT NULL COMMENT '物料规格',
  `standard` varchar(32) DEFAULT NULL COMMENT '物料型号',
  `min_stock` varchar(32) DEFAULT NULL COMMENT '最低库存',
  `max_stock` varchar(32) DEFAULT NULL COMMENT '最高库存',
  `verify_mode` varchar(32) DEFAULT NULL COMMENT '质检方式',
  `create_user` varchar(32) DEFAULT NULL COMMENT '创建人',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_user` varchar(32) DEFAULT NULL COMMENT '更新人',
  `update_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  `remarks` varchar(225) DEFAULT NULL COMMENT '备注',
  `del_flag` varchar(1) DEFAULT '0' COMMENT '删除标志',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

