/*Table structure for table `mdm_matrial_param_group` */

DROP TABLE IF EXISTS `mdm_matrial_param_group`;

CREATE TABLE `mdm_matrial_param_group` (
  `id` varchar(30) NOT NULL COMMENT 'id',
  `code` varchar(32) DEFAULT NULL COMMENT '物料编码',
  `name` varchar(50) DEFAULT NULL COMMENT '物料名称',
  `is_purchase` varchar(50) DEFAULT NULL COMMENT '是否可采购',
  `is_sale` varchar(50) DEFAULT NULL COMMENT '是否可销售',
  `is_produce` varchar(50) DEFAULT NULL COMMENT '是否可生产',
  `enable_warehouse` varchar(50) DEFAULT NULL COMMENT '启用仓库',
  `enable_region` varchar(50) DEFAULT NULL COMMENT '启用区域',
  `enable_batch` varchar(50) DEFAULT NULL COMMENT '启用批号',
  `enable_location` varchar(50) DEFAULT NULL COMMENT '启用库位',
  `min_stock` varchar(50) DEFAULT NULL COMMENT '最低安全库存',
  `max_stock` varchar(50) DEFAULT NULL COMMENT '最高安全库存',
  `out_principal` varchar(50) DEFAULT NULL COMMENT '出库原则',
  `default_warehouse` varchar(50) DEFAULT NULL COMMENT '默认仓库',
  `default_unit` varchar(50) DEFAULT NULL COMMENT '默认单位',
  `verify_mode` varchar(50) DEFAULT NULL COMMENT '检验方式',
  `sample_rate` varchar(50) DEFAULT NULL COMMENT '抽样比率',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT 'create_time',
  `update_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT 'update_time',
  `enable_serial` varchar(50) DEFAULT NULL COMMENT '启用序列号',
  PRIMARY KEY (`id`)
