/*Table structure for table `mdm_region` */

DROP TABLE IF EXISTS `mdm_region`;

CREATE TABLE `mdm_region` (
  `id` varchar(30) NOT NULL COMMENT 'id',
  `code` varchar(32) DEFAULT NULL COMMENT '区域编码',
  `name` varchar(32) DEFAULT NULL COMMENT '区域名称',
  `warehouse` varchar(32) DEFAULT NULL COMMENT '所属仓库',
  `charge_person` varchar(32) DEFAULT NULL COMMENT '负责人',
  `max_volume` varchar(20) DEFAULT NULL COMMENT '最大体积',
  `max_weight` varchar(20) DEFAULT NULL COMMENT '最大重量',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='区域';

